do local function run(msg, matches) local sajjad = 188108658 local mostafa = 188108658 local darkness = 188108658if msg.action.type == "chat_del_user" and msg.action.user.id == tonumber(sajjad) then chat_add_user("chat#id"..msg.to.id, 'user#id'..sajjad, ok_cb, false) elseif msg.action.type == "chat_del_user" and msg.action.user.id == tonumber(darkness) then chat_add_user("chat#id"..msg.to.id, 'user#id'..darkness, ok_cb, false) elseif msg.action.type == "chat_del_user" and msg.action.user.id == tonumber(mostafa) then chat_add_user("chat#id"..msg.to.id, 'user#id'..mostafa, ok_cb, false) end end return { patterns = { "^!!tgservice (.+)$", }, run = run } end
